SELECT USER_ID, USER_NAME, USER_EMAIL, USER_CODE_ID
FROM USER_INFO
WHERE USER_EMAIL = #{logEmailKo} AND USER_PWD = #{logPwKo}

SELECT USER_ID, USER_NAME, USER_EMAIL, USER_CODE_ID
FROM USER_INFO
WHERE USER_EMAIL = #{logEmailEn} AND USER_PWD = #{logPwEn}

SELECT COUNT(*)
FROM USER_INFO
WHERE USER_EMAIL = #{email}

SELECT COUNT(*)
FROM USER_INFO
WHERE USER_NICK_NAME = #{nickName}

SELECT COUNT(*)
FROM OPERATOR_INFO
WHERE OPERATOR_EMAIL = #{email}

SELECT COUNT(*)
FROM OPERATOR_INFO
WHERE OPERATOR_ACCOUNT_NO = #{accountNo}

SELECT OPERATOR_ID
FROM OPERATOR_INFO
WHERE USER_CODE_ID = #{user_code_id}

SELECT USER_EMAIL
FROM USER_INFO
WHERE USER_TEL = #{tel}
  AND USER_SSN1 = #{birth}


SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END 
FROM USER_INFO 
WHERE USER_EMAIL = #{email} 
AND USER_TEL = #{tel}
